	}

	$o=array('silentErrors' => true);
	sql('insert into `ads` set       `advertiser`=' . (($data['advertiser'] !== '' && $data['advertiser'] !== NULL) ? "'{$data['advertiser']}'" : 'NULL') . ', `adName`=' . (($data['adName'] !== '' && $data['adName'] !== NULL) ? "'{$data['adName']}'" : 'NULL') . ', ' . ($data['image']!='' ? "`image`='{$data['image']}'" : ($_POST['image_remove'] != 1 ? '`image`=`image`' : '`image`=NULL')) . ', `code`=' . (($data['code'] !== '' && $data['code'] !== NULL) ? "'{$data['code']}'" : 'NULL') . ', `text`=' . (($data['text'] !== '' && $data['text'] !== NULL) ? "'{$data['text']}'" : 'NULL') . ', `active`=' . (($data['active'] !== '' && $data['active'] !== NULL) ? "'{$data['active']}'" : 'NULL') . ', `StartDate`=' . (($data['StartDate'] !== '' && $data['StartDate'] !== NULL) ? "'{$data['StartDate']}'" : 'NULL') . ', `EndDate`=' . (($data['EndDate'] !== '' && $data['EndDate'] !== NULL) ? "'{$data['EndDate']}'" : 'NULL') . ', `catagory`=' . (($data['catagory'] !== '' && $data['catagory'] !== NULL) ? "'{$data['catagory']}'" : 'NULL'), $o);
	if($o['error']!=''){
