function advertisers_insert(){
	global $Translation;

	if($_GET['insert_x']!=''){$_POST=$_GET;}

	// mm: can member insert record?
	$arrPerm=getTablePermissions('advertisers');
	if(!$arrPerm[1]){
		return false;
	}

	$data['Name'] = makeSafe($_POST['Name']);
		if($data['Name'] == empty_lookup_value){ $data['Name'] = ''; }
	$data['AffilateCode'] = makeSafe($_POST['AffilateCode']);
		if($data['AffilateCode'] == empty_lookup_value){ $data['AffilateCode'] = ''; }
	$data['URL'] = makeSafe($_POST['URL']);
		if($data['URL'] == empty_lookup_value){ $data['URL'] = ''; }
	$data['Contact'] = makeSafe($_POST['Contact']);
		if($data['Contact'] == empty_lookup_value){ $data['Contact'] = ''; }
	$data['email'] = makeSafe($_POST['email']);
		if($data['email'] == empty_lookup_value){ $data['email'] = ''; }

	// hook: advertisers_before_insert
	if(function_exists('advertisers_before_insert')){
		$args=array();
		if(!advertisers_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('insert into `advertisers` set       `Name`=' . (($data['Name'] !== '' && $data['Name'] !== NULL) ? "'{$data['Name']}'" : 'NULL') . ', `AffilateCode`=' . (($data['AffilateCode'] !== '' && $data['AffilateCode'] !== NULL) ? "'{$data['AffilateCode']}'" : 'NULL') . ', `URL`=' . (($data['URL'] !== '' && $data['URL'] !== NULL) ? "'{$data['URL']}'" : 'NULL') . ', `Contact`=' . (($data['Contact'] !== '' && $data['Contact'] !== NULL) ? "'{$data['Contact']}'" : 'NULL') . ', `email`=' . (($data['email'] !== '' && $data['email'] !== NULL) ? "'{$data['email']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"advertisers_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID=mysql_insert_id();

	// hook: advertisers_after_insert
	if(function_exists('advertisers_after_insert')){
		$res = sql("select * from `advertisers` where `id`='" . makeSafe($recID) . "' limit 1", $eo);
		if($row = mysql_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID);
		$args=array();
		if(!advertisers_after_insert($data, getMemberInfo(), $args)){ return (get_magic_quotes_gpc() ? stripslashes($recID) : $recID); }
	}

	// mm: save ownership data
	sql("insert into membership_userrecords set tableName='advertisers', pkValue='$recID', memberID='".getLoggedMemberID()."', dateAdded='".time()."', dateUpdated='".time()."', groupID='".getLoggedGroupID()."'", $eo);

	return (get_magic_quotes_gpc() ? stripslashes($recID) : $recID);
}

function advertisers_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
