	}

	// hook: advertisers_before_delete
	if(function_exists('advertisers_before_delete')){
		$args=array();
		if(!advertisers_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	sql("delete from `advertisers` where `id`='$selected_id'", $eo);

	// hook: advertisers_after_delete
	if(function_exists('advertisers_after_delete')){
		$args=array();
		advertisers_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='advertisers' and pkValue='$selected_id'", $eo);
}

