function media_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0){
	// function to return an editable form for a table records
	// and fill it with data of record whose ID is $selected_id. If $selected_id
	// is empty, an empty form is shown, with only an 'Add New'
	// button displayed.

	global $Translation;

	// mm: get table permissions
	$arrPerm=getTablePermissions('media');
	if(!$arrPerm[1] && $selected_id==''){ return ''; }
	// print preview?
	$dvprint = false;
	if($selected_id && $_REQUEST['dvprint_x'] != ''){
		$dvprint = true;
	}


	// populate filterers, starting from children to grand-parents

	// unique random identifier
	$rnd1 = ($dvprint ? rand(1000000, 9999999) : '');
	// combobox: type
	$combo_type = new Combo;
	$combo_type->ListType = 0;
	$combo_type->MultipleSeparator = ', ';
	$combo_type->ListBoxHeight = 10;
	$combo_type->RadiosPerLine = 1;
	if(is_file(dirname(__FILE__).'/hooks/media.type.csv')){
		$type_data = addslashes(implode('', @file(dirname(__FILE__).'/hooks/media.type.csv')));
		$combo_type->ListItem = explode('||', entitiesToUTF8(convertLegacyOptions($type_data)));
		$combo_type->ListData = $combo_type->ListItem;
	}else{
		$combo_type->ListItem = explode('||', entitiesToUTF8(convertLegacyOptions("cd;;dvd;;vhs;;bluray;;cassette;;ps1;;ps2;;ps3;;ps4;;xbox;;xbox360;;xboxone;;wii;;other;;book;;poster;;iphone;;android;;tablet;;digital")));
		$combo_type->ListData = $combo_type->ListItem;
	}
	$combo_type->SelectName = 'type';
	$combo_type->AllowNull = false;
	// combobox: condition
	$combo_condition = new Combo;
	$combo_condition->ListType = 0;
	$combo_condition->MultipleSeparator = ', ';
	$combo_condition->ListBoxHeight = 10;
	$combo_condition->RadiosPerLine = 1;
	if(is_file(dirname(__FILE__).'/hooks/media.condition.csv')){
		$condition_data = addslashes(implode('', @file(dirname(__FILE__).'/hooks/media.condition.csv')));
		$combo_condition->ListItem = explode('||', entitiesToUTF8(convertLegacyOptions($condition_data)));
		$combo_condition->ListData = $combo_condition->ListItem;
	}else{
		$combo_condition->ListItem = explode('||', entitiesToUTF8(convertLegacyOptions("new;;used;;worn;;working;;for parts;;not working;;unboxed;;")));
		$combo_condition->ListData = $combo_condition->ListItem;
	}
	$combo_condition->SelectName = 'condition';
	$combo_condition->AllowNull = false;
	// combobox: date
	$combo_date = new DateCombo;
	$combo_date->DateFormat = "mdy";
	$combo_date->MinYear = 1900;
	$combo_date->MaxYear = 2100;
	$combo_date->DefaultDate = parseMySQLDate('<%%creationDate%%>', '<%%creationDate%%>');
	$combo_date->MonthNames = $Translation['month names'];
	$combo_date->NamePrefix = 'date';

	if($selected_id){
		// mm: check member permissions
		if(!$arrPerm[2]){
			return "";
		}
		// mm: who is the owner?
		$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='media' and pkValue='".makeSafe($selected_id)."'");
		$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='media' and pkValue='".makeSafe($selected_id)."'");
		if($arrPerm[2]==1 && getLoggedMemberID()!=$ownerMemberID){
			return "";
		}
		if($arrPerm[2]==2 && getLoggedGroupID()!=$ownerGroupID){
			return "";
		}

		// can edit?
		if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){
			$AllowUpdate=1;
		}else{
			$AllowUpdate=0;
		}

		$res = sql("select * from `media` where `id`='".makeSafe($selected_id)."'", $eo);
		$row = mysql_fetch_array($res);
		$urow = $row; /* unsanitized data */
		$hc = new CI_Input();
		$row = $hc->xss_clean($row); /* sanitize data */
		$combo_type->SelectedData = $row['type'];
		$combo_condition->SelectedData = $row['condition'];
		$combo_date->DefaultDate = $row['date'];
	}else{
		$combo_type->SelectedText = ( $_REQUEST['FilterField'][1]=='2' && $_REQUEST['FilterOperator'][1]=='<=>' ? (get_magic_quotes_gpc() ? stripslashes($_REQUEST['FilterValue'][1]) : $_REQUEST['FilterValue'][1]) : "");
		$combo_condition->SelectedText = ( $_REQUEST['FilterField'][1]=='5' && $_REQUEST['FilterOperator'][1]=='<=>' ? (get_magic_quotes_gpc() ? stripslashes($_REQUEST['FilterValue'][1]) : $_REQUEST['FilterValue'][1]) : "");
	}
	$combo_type->Render();
	$combo_condition->Render();

	// code for template based detail view forms

	// open the detail view template
	$templateCode = @file_get_contents('./templates/media_templateDV.html');

	// process form title
	$templateCode=str_replace('<%%DETAIL_VIEW_TITLE%%>', 'Media details', $templateCode);
	$templateCode=str_replace('<%%RND1%%>', $rnd1, $templateCode);
	// process buttons
	if($arrPerm[1]){ // allow insert?
